PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE cars (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        car_name TEXT NOT NULL,
        image_url TEXT,
        insurance_expiry DATE,
        tax_expiry DATE,
        maintenance_date DATE,
        license_plate TEXT
    , last_update_date DATE, is_available BOOLEAN DEFAULT 1);
CREATE TABLE driver_availabilitys (id INTEGER PRIMARY KEY AUTOINCREMENT, driver_name TEXT, start_datetime TEXT, end_datetime TEXT, id_bookings INTEGER);
CREATE TABLE chat_messages_mt (id INTEGER PRIMARY KEY AUTOINCREMENT, sender TEXT NOT NULL, message TEXT NOT NULL, timestamp TEXT DEFAULT CURRENT_TIMESTAMP, order_id INTEGER NOT NULL, image_user TEXT, image_path TEXT, FOREIGN KEY (order_id) REFERENCES work_orders (id));
CREATE TABLE chat_messages (id INTEGER PRIMARY KEY AUTOINCREMENT, sender TEXT NOT NULL, message TEXT NOT NULL, timestamp TEXT DEFAULT CURRENT_TIMESTAMP, ticket_id INTEGER, sender_id TEXT, image_path TEXT, image_user TEXT, FOREIGN KEY (ticket_id) REFERENCES tickets (id));
CREATE TABLE drivers (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT NOT NULL, phone TEXT, driver_image TEXT, license TEXT);
CREATE TABLE bookings (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT NOT NULL, email TEXT NOT NULL, car_type TEXT, description TEXT, start_datetime TEXT, end_datetime TEXT, bookstatus TEXT DEFAULT 'Pending', department TEXT, pickup TEXT, destination TEXT, status TEXT DEFAULT Pending, rider_name TEXT, need_driver TEXT, driver_endtime TEXT, start_timestap TEXT, accept_timestamp TEXT, inuse_timestamp TEXT);
CREATE TABLE chat_messages_nv (id INTEGER PRIMARY KEY AUTOINCREMENT, sender TEXT NOT NULL, message TEXT NOT NULL, timestamp TEXT DEFAULT CURRENT_TIMESTAMP, error_id INTEGER NOT NULL, image_user TEXT, image_path TEXT, document_path TEXT, FOREIGN KEY (error_id) REFERENCES navs (id));
CREATE TABLE table_data (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                error_id TEXT NOT NULL,
                table_data TEXT NOT NULL
            );
CREATE TABLE notifications (id INTEGER PRIMARY KEY AUTOINCREMENT, notification_type TEXT NOT NULL, notification_id INTEGER NOT NULL, message TEXT NOT NULL, timestamp DATETIME DEFAULT CURRENT_TIMESTAMP, creator_id INTEGER NOT NULL, noti_number TEXT);
CREATE TABLE navs (id INTEGER PRIMARY KEY UNIQUE NOT NULL, error_number TEXT NOT NULL UNIQUE, title TEXT NOT NULL, description TEXT NOT NULL, status TEXT NOT NULL DEFAULT 'open', created_at TEXT DEFAULT CURRENT_TIMESTAMP, close_at TEXT, department TEXT NOT NULL, reason TEXT, priority TEXT, issuetype TEXT, job_by TEXT, difftoinprogress TEXT, finish_by TEXT, nowerror_status TEXT, creator TEXT);
CREATE TABLE tasks (task_id INTEGER PRIMARY KEY AUTOINCREMENT, pickup_location TEXT, delivery_location TEXT, return_required BOOLEAN, status TEXT, created_at TEXT, pickup_lat REAL, pickup_lon REAL, delivery_lat REAL, delivery_lon REAL, pickup_address TEXT, delivery_address TEXT, queue TEXT);
CREATE TABLE employee (id INTEGER PRIMARY KEY AUTOINCREMENT, first_name TEXT, last_name TEXT, date_of_birth DATE, sex TEXT, employee_code INTEGER UNIQUE, start_work_date DATE, id_card_number TEXT, address TEXT, address_number TEXT, address_village TEXT, address_soi TEXT, address_road TEXT, address_sub_district TEXT, address_district TEXT, address_province TEXT, address_postcode TEXT, address_phone TEXT, contact_address TEXT, contact_address_number TEXT, contact_village TEXT, contact_soi TEXT, contact_road TEXT, contact_sub_district TEXT, contact_district TEXT, contact_province TEXT, contact_postcode TEXT, contact_phone TEXT, company_name TEXT, company_address TEXT, company_address_number TEXT, company_village TEXT, company_soi TEXT, company_road TEXT, company_sub_district TEXT, company_district TEXT, company_province TEXT, company_postcode TEXT, company_phone TEXT, position TEXT, department TEXT);
CREATE TABLE health_records (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    employee_id INTEGER NOT NULL,
    checkup_type TEXT NOT NULL, -- ตรวจสุขภาพครั้งแรก, ตรวจประจำปี, ตรวจเมื่อเปลี่ยนงาน, ตรวจเฝ้าระวังตามความจำเป็น
    checkup_date DATE NOT NULL,
    doctor_name TEXT NOT NULL,
    doctor_license_number TEXT NOT NULL,
    health_service_unit_name TEXT NOT NULL,
    health_service_unit_number TEXT NOT NULL,
    health_service_unit_address TEXT NOT NULL,
    health_service_unit_village TEXT,
    health_service_unit_soi TEXT,
    health_service_unit_road TEXT,
    health_service_unit_sub_district TEXT,
    health_service_unit_district TEXT,
    health_service_unit_province TEXT,
    health_service_unit_postcode TEXT,
    health_service_unit_phone TEXT,
    weight REAL,
    height REAL,
    bmi REAL,
    blood_pressure TEXT,
    pulse TEXT,
    physical_examination_result TEXT,
    lab_test_result BLOB, -- สามารถเก็บไฟล์หรือเอกสารได้
    FOREIGN KEY (employee_id) REFERENCES employee (id)
);
CREATE TABLE work_orders (id INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE, order_number TEXT NOT NULL UNIQUE, title TEXT NOT NULL, status TEXT, priority TEXT, department TEXT, description TEXT, issuetype TEXT, solution TEXT, created_at TEXT DEFAULT CURRENT_TIMSTAMP, completed_at TEXT, cost INTEGER, creator TEXT, responsible TEXT);
CREATE TABLE tickets (id INTEGER UNIQUE, ticket_number TEXT NOT NULL UNIQUE, title TEXT NOT NULL, description TEXT NOT NULL, status TEXT NOT NULL DEFAULT 'open', created_at TEXT DEFAULT CURRENT_TIMESTAMP, close_at TEXT, department TEXT NOT NULL, reason TEXT, type TEXT, issuetype TEXT, creator TEXT, subtype TEXT, reason_type TEXT, total_time TEXT, PRIMARY KEY (id AUTOINCREMENT), UNIQUE (id));
CREATE TABLE users (id INTEGER, username TEXT NOT NULL, password TEXT NOT NULL, department TEXT NOT NULL, displayname TEXT, roles TEXT DEFAULT user, image_path TEXT DEFAULT '/uploads/img_avatar.png', status TEXT, PRIMARY KEY (id AUTOINCREMENT));
DELETE FROM sqlite_sequence;
CREATE TRIGGER calculate_total_time AFTER UPDATE OF close_at ON tickets FOR EACH ROW WHEN NEW.close_at IS NOT NULL
 BEGIN UPDATE tickets
    SET total_time = (
        SELECT CASE
            -- ถ้าเวลามากกว่า 24 ชั่วโมง จะแสดงวัน ชั่วโมง นาที วินาที
            WHEN total_seconds >= 86400 THEN
                printf('%d d %02d h %02d m %02d s', 
                    total_seconds / 86400,                    -- วัน
                    (total_seconds % 86400) / 3600,           -- ชั่วโมง
                    (total_seconds % 3600) / 60,              -- นาที
                    total_seconds % 60                        -- วินาที
                )
            ELSE
                printf('%02d h %02d m %02d s', 
                    (total_seconds / 3600),                   -- ชั่วโมง
                    (total_seconds / 60) % 60,                -- นาที
                    total_seconds % 60                        -- วินาที
                )
        END
        FROM (
            SELECT (strftime('%s', NEW.close_at) - strftime('%s', OLD.created_at)) AS total_seconds
        )
    )
    WHERE id = NEW.id; END
;
CREATE UNIQUE INDEX idx_unique_order_id ON work_orders (order_number);
COMMIT;
