INSERT INTO chat_messages VALUES(1275,'Worrakan Nasai (Jack)',replace(replace('3. ตรวจสอบข้อมูลที่ต้องการบันทึก\r\nตรวจสอบข้อมูล: ให้แน่ใจว่าข้อมูลในไฟล์ Excel มีรูปแบบที่ถูกต้องตามประเภทข้อมูลที่ฐานข้อมูลต้องการ\r\nจัดรูปแบบข้อมูล: ใช้ฟังก์ชันใน Excel เช่น Data Validation เพื่อให้ข้อมูลถูกต้องก่อนการบันทึก','\r',char(13)),'\n',char(10)),'26-09-2024 18:32',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1276,'Worrakan Nasai (Jack)',replace(replace('4. ตรวจสอบและแก้ไข SQL Query\r\nตรวจสอบ SQL Query: ให้แน่ใจว่า SQL Query ที่ใช้ไม่มีข้อผิดพลาด เช่น การใช้ชื่อฟิลด์หรือชื่อเทเบิลผิด','\r',char(13)),'\n',char(10)),'26-09-2024 18:32',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1278,'Worrakan Nasai (Jack)',replace(replace('5. ตรวจสอบข้อผิดพลาดใน Excel\r\nบันทึกไฟล์ Excel แบบอื่น: ลองบันทึกไฟล์ในรูปแบบอื่น เช่น CSV หรือ Excel (xlsx) เพื่อตรวจสอบว่าไฟล์มีปัญหาหรือไม่\r\nเปิดในโหมด Safe Mode: เปิด Excel ในโหมด Safe Mode เพื่อดูว่ามี Add-ins ที่ทำให้เกิดปัญหาหรือไม่','\r',char(13)),'\n',char(10)),'26-09-2024 18:33',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1279,'Worrakan Nasai (Jack)',replace(replace('6. ตรวจสอบการล็อกไฟล์\r\nปิดโปรแกรมที่ใช้ไฟล์: ให้แน่ใจว่าไม่มีโปรแกรมอื่นที่เปิดไฟล์ Excel ที่คุณพยายามบันทึกอยู่\r\nรีสตาร์ทคอมพิวเตอร์: หากยังไม่สามารถบันทึกได้ อาจลองรีสตาร์ทคอมพิวเตอร์เพื่อล้างล็อก','\r',char(13)),'\n',char(10)),'26-09-2024 18:33',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1280,'Worrakan Nasai (Jack)',replace(replace('1. Check Database Connection\r\nVerify connection details: Ensure that the server, database name, username, and password are correct.\r\nTest the connection: Use tools like SQL Server Management Studio or pgAdmin to ensure the connection works properly.','\r',char(13)),'\n',char(10)),'26-09-2024 18:36',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1281,'Worrakan Nasai (Jack)',replace(replace('2. Check User Permissions\r\nEnsure proper permissions: Make sure the user account connecting to the database has sufficient privileges to perform operations like INSERT, UPDATE, or DELETE.','\r',char(13)),'\n',char(10)),'26-09-2024 18:36',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1282,'Worrakan Nasai (Jack)',replace(replace('3. Validate Data to be Saved\r\nCheck data types: Ensure that the data in the Excel file matches the SQL database field types.\r\nData formatting: Use Excel features like Data Validation to clean and format data before saving it.','\r',char(13)),'\n',char(10)),'26-09-2024 18:36',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1283,'Worrakan Nasai (Jack)',replace(replace('4. Review and Correct SQL Query\r\nCheck SQL Query syntax: Ensure the SQL statements are correctly written, with no mistakes in field names or table references.','\r',char(13)),'\n',char(10)),'26-09-2024 18:36',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1284,'Worrakan Nasai (Jack)',replace(replace('5. Check for Excel Issues\r\nTry saving the file in another format: Try saving the file as a CSV or a different Excel version to see if the issue persists.\r\nOpen Excel in Safe Mode: Open Excel in Safe Mode to check if any Add-ins are causing the issue.','\r',char(13)),'\n',char(10)),'26-09-2024 18:36',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
INSERT INTO chat_messages VALUES(1285,'Worrakan Nasai (Jack)',replace(replace('6. File Lock Issues\r\nClose other programs: Make sure no other program is accessing the Excel file.\r\nRestart the computer: If the file remains locked, restarting the system might help resolve the issue.','\r',char(13)),'\n',char(10)),'26-09-2024 18:36',1071,NULL,NULL,'/static/uploads/image_profile_1_20240717_200339.png');
